function event_say(e)
	if(e.message:findi("hail")) then
		if(e.other:HasItem(200001)) then -- Tome of Great Deeds. Your item ID may be different.
			e.self:Say("I am interested in hearing your tales of adventure!");
		else
			e.self:Say("Return to me when you have more tales of adventure to share.");
		end
	end
end

