		-- Set skills/discs based on class
		for i, curspell in ipairs(spells) do
			e.other:ScribeSpell(curspell,i-1,true);
			e.other:TrainDiscBySpellID(curspell);
		end
		-- Summon items based on class
		for i, curitem in ipairs(items) do
			e.other:SummonItem(curitem);
		end
	end
end
